import React, { useState } from "react";

import { GenericStyle, Styles } from '../app_styles';
import { Table } from "./Table";
import { Tabs, Tab, Content } from './Tabs';
//import DateFilter from './Filters/DateFilter';

import namor from 'namor'
import { useNavigate, useHistory, Redirect, Outlet } from "react-router-dom";
import { SliderColumnFilter, SelectColumnFilter, DateFilters } from "../Filters/Filters";
import { getDataFromServer, getColumnsWithFilters } from "../utils/dataFromServer"
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";
//import { TPage } from "./Tpage";
import { NavigationBar } from "./NavigationBar"
import Title from "./Title"
import { ConstructionOutlined } from "@mui/icons-material";
import { Responsive, WidthProvider } from "react-grid-layout"
import "react-grid-layout/css/styles.css"
import "react-resizable/css/styles.css"
//https://www.robinwieruch.de/react-router-nested-routes/



const getFormattedPrice = (price) => `$${price.toFixed(2)}`;




export function Home() {
  const len = 100
  const data = getDataFromServer(process.env.REACT_APP_MAIN_TABLE_ROUTE);//React.useMemo(() => makeData(len), [])
  //const navigate = useNavigate();

  // const route_to_Tpage = e => {
  //   //console.log("blaaaa")
  //   // console.log(this.props.value)
  //   navigate("TPage")
  //   // navigate("TPage/${this.state.selectedGenreName}/${this.state.selectedGenre}",{
  //   //   state: {
  //   //     data: e
  //   //   }
  //   // }); // TODO: rename this
  // }


  const [checkedState, setCheckedState] = useState(
    new Array(data.length).fill(false)
  );

  const [total, setTotal] = useState(0);

  const handleOnChange = (position) => {
    console.log("handleOnChange")
    const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
    console.log("handleOnChange2")
    setCheckedState(updatedCheckedState);
    console.log("handleOnChange3")
    const totalPrice = updatedCheckedState.reduce(
      (sum, currentState, index) => {
        if (currentState === true) {
          return sum + data[index].price;
        }
        return sum;
      },
      0
    );
  }

   // setTotal(totalPrice);

  const columns = React.useMemo( //useMemo returns a memoized value- runs when one of its dependencies update.
    () => [
      {
        Header: process.env.REACT_APP_FIRST_HEADER_MAIN_TABLE,
        columns: [
          {
            Header: '',
            accessor: 'checkboxcol',
            show: false,
            Cell: ({ cell }) => (
              <Link to={`/Home/TPage/${cell.pk}`}>
                <input type="checkbox" 
                // checked={checkedState[cell.pk]}
                // onChange={() => handleOnChange(cell.pk)}
                />
              </Link>
            )
          },
          {
            Header: 'PK',
            accessor: 'pk',
            show: false,
          },
          {
            Header: 'Type',
            accessor: 'type',
            show: false,
          },
          {
            Header: 'Name',
            accessor: 'name',
            // Use our custom `fuzzyText` filter on this column

            ////onClick={route_to_Tpage}
            filter: 'fuzzyText',
            Cell: ({ cell }) => (
              <Link to={`/Home/TPage/${cell.value}`} style={{ color: 'inherit', textDecoration: 'inherit' }}>
                {/* <button value={cell.value} >  */}
                {cell.value}
                {/* </button> */}
              </Link>

            )
          },
        ],
      },
      {
        Header: process.env.REACT_APP_SECOND_HEADER_MAIN_TABLE,
        columns: [
          {
            Header: 'Version',
            accessor: 'version',
            Filter: SliderColumnFilter,
            filter: 'equals',
          },
          /*  {
              Header: 'Visits',
              accessor: 'visits',
              Filter: NumberRangeColumnFilter,
              filter: 'between',
            },*/
          {
            Header: 'Status',
            accessor: 'status',
            Filter: SelectColumnFilter,
            filter: 'includes',

            // formatter: AddButtonToCell,
          },
          /*  {
              Header: 'Profile Progress',
              accessor: 'progress',
              Filter: SliderColumnFilter,
              filter: filterGreaterThan,
            },*/
          {
            Header: 'Date',
            accessor: 'date',
            Filter: DateFilters,
            filter: 'dateFilter'
          },

        ],
      },
    ],
    []
  )
  const ResponsiveReactGridLayout = WidthProvider(Responsive)
  const layouts  = [
      {  x: 100, y: 50, w: 20, h: 20 },
      {  x: 70, y: 70, w: 20, h: 20 },
      {  x: 90, y: 90, w: 20, h: 20 },
    ]

 

  //const data = React.useMemo(() => makeData(len), [])
  //const columns_names = getColumnsNames(data); //use memo dont goog here
  const columns_names = ['p', 'o']
  const [active, setActive] = useState(0);
  const handleClick = e => {
    const index = parseInt(e.target.id, 0);
    if (index !== active) {
      setActive(index);
    }
  };
  return (

    <GenericStyle>
      <NavigationBar />
      {/* <Tabs>
       <Tab onClick={handleClick} active={active === 0} id={0}>
          A
        </Tab>
       <Tab onClick={handleClick} active={active === 1} id={1}>
          CX
        </Tab>
        <Tab onClick={handleClick} active={active === 2} id={2}>
          Updates
        </Tab>
        <Tab onClick={handleClick} active={active === 3} id={3}>
          O
        </Tab>
      </Tabs> */}

      <Title message="Welcom to A" />
      <ResponsiveReactGridLayout
            // onLayoutChange={handleModify}
                verticalCompact={true}
                layout={layouts}
                breakpoints={{ lg: 1200, md: 996, sm: 768, xs: 480, xxs: 0 }}
                preventCollision={false}
                cols={{ lg: 8, md: 8, sm: 4, xs: 2, xxs: 2 }}
                autoSize={true}
                margin={{
                lg: [20, 20],
                md: [20, 20],
                sm: [20, 20],
                xs: [20, 20],
                xxs: [20, 20],
                }}
            >
      <Table columns={columns} data={data} id_t={0} />
      </ResponsiveReactGridLayout>


    </GenericStyle>


  )
}




//export default App
