import React from "react";
import { useTable, useFilters, useGlobalFilter } from 'react-table'
import { GlobalFilter, DefaultColumnFilter, fuzzyTextFilterFn, IndeterminateCheckbox } from "../Filters/Filters";
import "../Styles/Table.css"


// Let the table remove the filter if the string is empty
fuzzyTextFilterFn.autoRemove = val => !val



// Our table component
export function Table({ columns, data,id_t }) {
  console.log("columns:")
  console.log(columns)
  console.log("data:")
  console.log(data)
  const filterTypes = React.useMemo(
    () => ({
      // Add a new fuzzyTextFilterFn filter type.
      fuzzyText: fuzzyTextFilterFn,
      //date filter
      dateFilter: (rows, id, filterValue) => {
        return rows = rows.filter(row => {
          return new Date(row.values.date) >= filterValue[0] && new Date(row.values.date) <= filterValue[1];
        });
      },
      // Or, override the default text filter to use
      // "startWith"
      text: (rows, id, filterValue) => {
        return rows.filter(row => {
          const rowValue = row.values[id]
          return rowValue !== undefined
            ? String(rowValue)
              .toLowerCase()
              .startsWith(String(filterValue).toLowerCase())
            : true
        })
      },
    }),
    []
  )


  const defaultColumn = React.useMemo(
    () => ({
      // Let's set up our default Filter UI
      Filter: DefaultColumnFilter,
    }),
    []
  )

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    allColumns,
    state,
    visibleColumns,
    getToggleHideAllColumnsProps,
    preGlobalFilteredRows,
    setGlobalFilter,
  } = useTable(
    {
      columns,
      data,
      defaultColumn, // Be sure to pass the defaultColumn option
      filterTypes,
    },
    useFilters, // useFilters!
    useGlobalFilter // useGlobalFilter!
  )

  // We don't want to render all of the rows for this example, so cap
  // it for this use case
  const firstPageRows = rows.slice(0, 10)
  return (
    <React.Fragment>
      <div id="toggle_columns">
        <div class="row">
          <div class="column">
            <IndeterminateCheckbox {...getToggleHideAllColumnsProps()} /> Toggle
            All
          </div>

          {allColumns.map(column => (
            <div key={column.id} class="column">
              <label>
                <input type="checkbox" {...column.getToggleHiddenProps()} />{''}
                {column.id}
              </label>
            </div>
          ))}
          <br />
        </div>
      </div>
      <table id={"table"+id_t} {...getTableProps()}>
        <thead>
          <tr>
            <th
              colSpan={visibleColumns.length}
              style={{
                textAlign: 'left',
                background: '#282c34'
              }}
            >
              <GlobalFilter
                preGlobalFilteredRows={preGlobalFilteredRows}
                globalFilter={state.globalFilter}
                setGlobalFilter={setGlobalFilter}
              />
            </th>
          </tr>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                <th {...column.getHeaderProps()}>
                  {column.render('Header')}
                  {/* Render the columns filter UI */}
                  <div>{column.canFilter ? column.render('Filter') : null}</div>
                </th>
              ))}
            </tr>
          ))}

        </thead>
        <tbody {...getTableBodyProps()}>
          {firstPageRows.map((row, i) => {
            prepareRow(row)
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map(cell => {
                  return <td  {...cell.getCellProps()}>{cell.render('Cell')}</td>
                })}
              </tr>
            )
          })}
        </tbody>
      </table>
      <br />
    </React.Fragment>
  )
}
