import React, { Fragment, useState } from 'react';
import { GenericStyle } from '../app_styles';
import { NavigationBar } from './NavigationBar';
import Title from './Title';
import BaseInformation from './BaseInformation';
import { useParams, Link } from 'react-router-dom';
import { getDataFromServer, getColumnsWithFilters } from "../utils/dataFromServer";
import SubTitle from "./SubTitle";
import { Table } from './Table';
import { SliderColumnFilter, SelectColumnFilter, DateFilters } from "../Filters/Filters";
import DateFilter from '../Filters/DateFilter';
import { Scrollbars } from 'react-custom-scrollbars-2';
import download from '../images/download.png';
import Sidebar from './Sidebar';
//import download from '../images/download.png';
//import NotifyMe from 'react-notification-timeline';
import { Responsive, WidthProvider } from "react-grid-layout"
import "react-grid-layout/css/styles.css"
import "react-resizable/css/styles.css"
import GridLayout from "react-grid-layout";


//import { NotificationApp } from "./NotificationApp"
export function TPage() {
    const { t_id } = useParams();
    let data = getDataFromServer('/t');
    data = data.slice(1)  //slice(1) because we dont need the message for how many return results

    let columns_to_delete;
    let columns, rows;
    let id_couunter=0;
    // return(
    //     <Fragment>
    //         <Table1><Row></Row><Row></Row><Row></Row></Table1>
    //         <Table2><Row></Row><Row></Row><Row></Row></Table2>
    //         <Table3><Row></Row><Row></Row><Row></Row></Table3>
    //     </Fragment>
    // )

{/* <ReactGridLayout
          {...this.props}
          onLayoutChange={layout => this.setState({ layout })}
        >
          {this.state.layout.map(item => (
            <div key={item.i} data-grid={item}>
              <span>{item.i}</span>
            </div>
          ))}
        </ReactGridLayout> */}
       
     
        
    const ResponsiveReactGridLayout = WidthProvider(Responsive)
    const layout = [
        { i: "a", x: 150, y: 0, w: 100, h: 100, static: true },
        { i: "b", x: 200, y: 0, w: 3, h: 2, minW: 2, maxW: 4 },
        { i: "c", x: 4, y: 0, w: 1, h: 2 }
      ];
    
    return (<GenericStyle>
        <NavigationBar />
        {/* <NotificationApp></NotificationApp>*/}
        {/*  <Scrollbars style={{ width: 500, height: 300 }}>*/}
        <Sidebar pageWrapId={'page-wrap'} outerContainerId={'outer-container'} />
        <Title message="Summery of" name="t_name" pk={t_id} />
        <BaseInformation />
        <Fragment key="tpage_id">
            <GridLayout
            className="layout"
            layout={layout}
            cols={12}
            rowHeight={30}
            width={1200}
            color="green"
        >
               <div key="a">
         
            {data.map(function (item) {
                if (Object.keys(item).length != 0) {
                    id_couunter=id_couunter+1
                    console.log("here2")
                    console.log(item.type.normalize() === 'table'.normalize())
                    if (item.type.normalize() === 'table'.normalize()) {
                        console.log("here3")
                        columns = item.data.headers
                        rows = item.data.rows
                        let columns_with_filters = getColumnsWithFilters(columns, process.env.REACT_APP_TPAGE_ROUTE, download) //TODO CHECK HOW CAN DO IT WITH React.useMemo(() => {
                        return <Table columns={columns_with_filters} data={rows} id_t={id_couunter} />
                    } else if (item.type.normalize() === 'message'.normalize()) {
                        return <SubTitle title={item.data} />
                    }
                }
            })
            }
               </div>
            </GridLayout>
        </Fragment>
    </GenericStyle>
    )
}
